package conversacion;

public class Dialogo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("AMADOR:(mirando el reloj) Lleva horas fuera. ¿Y si le ha pasado algo?"
				+ "¡Vicente nunca desaparece tanto tiempo sin dejar rastro!");
		System.out.println("MAXI: (con su tono calmado y algo pedante) Amador, no subestimes mis habilidades deductivas. He analizado la situación."
				+ " Vicente no es hombre de aventuras espontáneas. Si ha salido, es por algo importante");
		System.out.println("AMADOR:(exasperado) ¿Importante?"
				+ "¡Por favor! La última vez que dijo que hacía algo importante estaba buscando una carta de amor de Gregoria en la basura.");
		System.out.println("MAXI: (señalando un rastro en la alfombra) ¡Ajá! Esto confirma mi teoría. "
				+ "¿Ves esas migas? Claramente son de croqueta. Gregoria siempre usaba pan rallado casero. Vicente ha salido con la intención de despedirse de algo.\r\n"
				);
		System.out.println("AMADOR: (sarcástico) ¿Qué eres, Sherlock de Hacendado?"
				+ "Es un viernes cualquiera, seguro que se ha perdido en el bar y no encuentra el camino a casa.");
		System.out.println("MAXI: (ofendido) Amador, no olvides que fui el fiel ayudante de Sherlock Holmes."
				+ " La precisión es mi especialidad. Según el grosor y el tamaño de las migas, Vicente se dirige hacia el parque.\r\n"
				);
		System.out.println(
				"AMADOR:(suspirando) Vale, Sherlock. Vamos a buscarle antes de que se nos convierta en leyenda urbana. ");

		System.out.println("VICENTE: (hablando consigo mismo) La última croqueta de mi Gregoria. "
				+ "Siempre decía que las hacía con amor… y vaya si tenía razón"
				+ "Ayyy Gregoria como te echo de menos.");

		System.out.println("AMADOR:(llegando corriendo) ¡Vicente! ¡Pero qué haces aquí, hombre!"
				+ "¡Nos has tenido con el corazón en un puño!");
		System.out.println("MAXI: (examinando a Vicente) Hmm... Cara pálida, ojos llorosos... "
				+ "Está claro, este hombre está en un viaje emocional."
				);
		System.out.println("VICENTE: (melancólico) Aquí estoy, despidiéndome de mi Gregoria. "
				+ "Esta croqueta fue lo último que cocinó para mí.");

		System.out.println("AMADOR: (sentándose junto a él) Venga, Vicentico. Gregoria querría que estuvieras bien."
				+ "Además, no puedes despedirte de la croqueta con el estómago vacío.");
		System.out.println("MAXI: (mirando el lago) Aunque emocionalmente comprensible, "
				+ "estar aquí solo no es la solución. Lo que necesitas es compañía y una buena caña."
				);
		System.out.println("VICENTE: Vicente: (esbozando una sonrisa) ¿Cañas? " + "Eso suena a algo que Gregoria aprobaría.");

		System.out.println("MAXI: (también brindando) ¡Y por nosotros, los leones de Montepinar, "
				+ "que siempre encontramos el camino de vuelta!"
				);
		System.out.println("VICENTE: (emocionado) Y por la amistad. "
				+ "Con vosotros, hasta lo más triste se convierte en algo llevadero.");

		System.out.println("AMADOR: (levantando su caña) ¡Por Gregoria, la reina de las croquetas y el amor eterno!");

		System.out.println("FIN");

	}

}
